import React from 'react';
import formatPhoneNumber from '../../untils/formatPhoneNumber';

class InputPhone extends React.Component {
  constructor(props) {
    super(props);
    this.state = { phone: '+375(__)___-__-__' };
  }

  render() {
    const {
      className, name, type, minLength, maxLength, placeholder,
    } = this.props;
    const { phone } = this.state;
    const { setClientInformation } = this.setClientInformation;

    return (
      <>
        <input
          value={phone}
          onFocus={() => setClientInformation({ phone: '+375' })}
          onChange={(event) => setClientInformation({
            phone: formatPhoneNumber(event.target.value),
          })}
          className={className}
          name={name}
          type={type}
          minLength={minLength}
          maxLength={maxLength}
          placeholder={placeholder}
        />
      </>
    );
  }
}

export default InputPhone;
