import { React, useState } from 'react';
import { Link } from 'react-router-dom';
import { useComponentVisible, LoginForm } from '../../../../common/loginForm/components/LoginFormComponent';
import logo from '../../../../assets/header/logo.png';
import basket from '../../../../assets/common/basket.png';

const Header = ({
  linkItem, link, disabled,
}) => {
  const [loginFormActive, setLoginForm] = useState(false);
  const {
    ref,
    isComponentVisible,
    setIsComponentVisible,
  } = useComponentVisible(true);

  return (
    <div className="header-wrap">
      <div className="header-box">
        <div className="logo">
          <img src={logo} alt="Все для дома" title="Все для дома" />
        </div>
        <div className={!disabled ? 'basket' : 'basket -disabled'}>
          <Link to="/basket">
            <img src={basket} alt="Корзина" title="Корзина" />
          </Link>
        </div>
        <Link to={link}>
          {linkItem}
        </Link>
        <div className="loginDiv">
          <button
            className="buttonLogin"
            type="button"
            onClick={() => {
              isComponentVisible ? setIsComponentVisible(false) : setIsComponentVisible(true);
            }}
          >
            Вход / регистрация
          </button>
        </div>
        { isComponentVisible
          ? (
            <LoginForm
              setLoginForm={setLoginForm}
            />
          )
          : null}
      </div>
    </div>
  );
};

export default Header;
