import axios from 'axios';
import serverUrl from '../../../../common/constants/urls';

const ordersHistory = '/orders-history';

async function postHistoryOrder(id, updateData) {
  const payload = {
    data: {
      id,
    },
  };

  try {
    const response = await axios.post(`${serverUrl}${ordersHistory}`, payload);
    const result = response.data;

    /*    updateData('ordersArray', result); */
    updateData('isLoading', false);
    console.log(result);
    return result;
  } catch (error) {
    updateData('error', error.message);
    return null;
  }
}

export default postHistoryOrder;
