import { createStore, applyMiddleware } from 'redux';
import logger from 'redux-logger';

const create = () => {
    const store = createStore([], applyMiddleware(logger));

    return store
};

const store = create();

store.dispatch({
  type: 'ADD_TODO',
  payload: {
    text: 'Hello, World',
  },
});

export default store;
