/* eslint-disable react/destructuring-assignment */
import React from 'react';
import PopUp from '../../../common/popup/components/PopUpComponent';
import PopUpSomethingWentWrong from '../../../common/popup/components/PopUpSomethingWentWrongComponent';
import postProductsCategory from '../api/post/postProductsCategory';
import getProducts from '../api/get/getProducts';

class InputEditProductsInStock extends React.Component {
  constructor(props) {
    super(props);
    const { inStock, id } = this.props;
    this.state = {
      id,
      inStock,
      productsArray: [],
      errorMessage: '',
      isLoading: false,
      popupSmthWentWrongActive: true,
    };
    this.setError = this.setError.bind(this);
    this.updateData = this.updateData.bind(this);
    this.setpopupSmthWentWrongActive = this.setpopupSmthWentWrongActive.bind(this);
  }

  componentDidMount() {
    this.interval = setTimeout(async () => {
      await getProducts(this.updateData, this.setError);
    }, 10);
  }

  async handleCategoryProductsChange(e) {
    e.preventDefault();

    this.setState({
      categoryName: e.target.value,
    });
    postProductsCategory(this.state.id, e.target.value);
  }

  setError(value) {
    this.setState({
      errorMessage: value,
    });
  }

  setpopupSmthWentWrongActive(value) {
    this.setState({
      popupSmthWentWrongActive: value,
    });
  }

  updateDataCategories(value, valueIsLoading) {
    this.setState({
      productsArray: value,
      isLoading: valueIsLoading,
    });
  }

  render() {
    const {
      inStock, productsArray, errorMessage,
      isLoading, popupSmthWentWrongActive,
    } = this.state;

    if (!isLoading) {
      return <div className="-isLoading"> </div>;
    }
    if (errorMessage) {
      return (
        <PopUp
          active={popupSmthWentWrongActive}
          setActive={this.setpopupSmthWentWrongActive}
        >
          <PopUpSomethingWentWrong
            text="Попробуйте еще раз"
            setpopupSmthWentWrongActive={this.setpopupSmthWentWrongActive}
          />
        </PopUp>
      );
    }

    return (
      <>
        <select
          className="productsCategory"
          value={this.state.categoryName}
          onChange={async (e) => {
            this.handleCategoryProductsChange(e);
          }}
        >
          {productsArray.products.map((product) => (
            this.props.inStock === product.inStock
              ? (
                <option
                  key={product.id}
                  value={product.inStock}
                >
                  {Number(product.inStock) === 1 ? 'да' : 'нет'}
                </option>
              )

              : <option key={category.id} value={category.category}>{Number(product.inStock) === 1 ? 'да' : 'нет'}</option>
          ))}
        </select>

      </>
    );
  }
}

export default InputEditProductsInStock;
