const test = (event, divRef) => {
  if (divRef.current.contains(event.target)) {
    this.setState({ isVisible: true });
    console.log('click inside');
  } else {
    this.setState({ isVisible: false });
    console.log('clickes outdside');
  }
};
export default test;
