import { ADD, INCREASE, DECREASE } from '../actions/catalogItemsActions';

const initialState = [];

export default (state = initialState, action) => {
  switch (action.type) {
    case ADD.type:
      return [...state, action.payload.item];

    case INCREASE.type:
      return [...state, action.payload.item.counter + 1];

    case DECREASE.type:
      return [...state, action.payload.item.counter - 1];

    default:
      return state;
  }
};
