// eslint-disable-next-line no-unused-vars
import React from 'react';

function formatterNumb(numb) {
  if (numb.indexOf() === 1 || numb.indexOf() === 2 || numb.indexOf() === 3) {
    return `+(${numb})`;
  }

  if (numb.indexOf() === 2) {
    return `+(${numb})`;
  }
}

function formatPhoneNumber(stringPhone) {
  stringPhone.addEventListener('input', function (event) {
    let number = '';
    for (const string of this.value) {
      if ('0123456789'.includes(string)) {
        number += string;
      }
    }

    number = parseInt(number);

    if (number < minValue) {
      number = minValue;
    }

    if (number > maxValue) {
      number = maxValue;
    }

    number = formatterNumb(number);

    this.value = number;
  });
}

export default formatPhoneNumber;
