import React from 'react';
import Header from '../../main/header/components/HeaderComponent';
import Footer from '../../main/footer/components/FooterComponent';
import AddCategoryImage from './AddCategoryImageComponent';

class AddCategoryPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      categoryName: '',
    };
  }

  render() {
    const { categoryName } = this.state;
    return (
      <>
        <Header linkItem={<button type="button" className="buttonBack">Назад</button>} link="/personal" disabled={false} />
        <div className="addCategory-box">
          <h2 className="">Добавить категорию</h2>
          <div className="addCategory-container">
            <p className="categoryNameString">Название</p>
            <input
              type="text"
              className="categoryNameInput"
              value={categoryName}
            />
            <p className="categoryColorString">Цвет заголовка</p>
            <select className="imageColors">
              <option value="1" selected>Фиолетовый</option>
              <option>Коричневый</option>
              <option>Зеленый</option>
            </select>
            <AddCategoryImage />
            <button
              className="addNewCategoryButton"
              type="button"
            >
              Добавить
            </button>
          </div>
        </div>
        <Footer />
      </>
    );
  }
}

export default AddCategoryPage;
