import React from 'react';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clientLogin: '',
      clientPassword: '',
    };
  }

  render() {
    const {
      clientLogin, clientPassword,
    } = this.state;
    const { setLoginFormLoginPage, setLoginLoginFormForgetPassword } = this.props;
    return (
      <form className="form">
        <div className="login">
          <p className="loginString">Логин:</p>
          <input
            className="loginInput"
            id="loginInput"
            type="text"
            name="LOGIN"
            value={clientLogin}
            onChange={((event) => this.setState({ clientLogin: event.target.value }))}
          />
          <svg className="errorSymbol " viewBox="0 0 14.98 15" id="errorSymbol">
            <g>
              <path d="M7.49,0A7.5,7.5,0,1,0,15,7.51,7.49,7.49,0,0,0,7.49,0Zm0,14.27a6.77,6.77,0,1,1,6.76-6.76A6.78,6.78,0,0,1,7.49,14.27Zm.37-3.71a.39.39,0,1,1-.39-.39A.39.39,0,0,1,7.86,10.57ZM7.1,8.65V4.23a.35.35,0,1,1,.7,0V8.65A.35.35,0,0,1,7.45,9,.34.34,0,0,1,7.1,8.65Z" />
            </g>
          </svg>
        </div>
        <div className="password">
          <p className="passwordString">Пароль:</p>
          <input
            className="passwordInput"
            id="passwordInput"
            type="password"
            name="PASSWORD"
            value={clientPassword}
            onChange={((event) => this.setState({ clientPassword: event.target.value }))}
          />
          <svg className="errorSymbol " viewBox="0 0 14.98 15" id="errorSymbol">
            <g>
              <path d="M7.49,0A7.5,7.5,0,1,0,15,7.51,7.49,7.49,0,0,0,7.49,0Zm0,14.27a6.77,6.77,0,1,1,6.76-6.76A6.78,6.78,0,0,1,7.49,14.27Zm.37-3.71a.39.39,0,1,1-.39-.39A.39.39,0,0,1,7.86,10.57ZM7.1,8.65V4.23a.35.35,0,1,1,.7,0V8.65A.35.35,0,0,1,7.45,9,.34.34,0,0,1,7.1,8.65Z" />
            </g>
          </svg>
        </div>
        <button
          type="button"
          className="entranceButton"
        >
          Войти
        </button>
        <button
          type="button"
          className="forgotPasswordButton"
          onClick={() => {
            setLoginFormLoginPage(false);
            setLoginLoginFormForgetPassword(true);
          }}
        >
          Забыли пароль?
        </button>
        <button
          type="button"
          className="registrationButton"
        >
          Регистрация
        </button>
      </form>

    );
  }
}

export default Login;
