import CatalogItemsActions from '../actions/catalogItemsActions';

const initialState = {
  items: [],
};

export default (state = initialState, action) => {
  switch (action.type) {
    case CatalogItemsActions.ADD:
      return {
        ...state,
        items: [...state.items, action.payload],
      };

    default:
      return state;
  }
};
