  let phone = '';

  value.split('').forEach((letter, index) => {
    if (index === 4) {
      phone += `(${letter}`;
    }

    if ('+0123456789'.includes(letter)) {
      phone += letter;
    }
  });
  return phone;
}

export default formatPhoneNumber;
