const updateDataCategoriesProducts = (isMounted, category, setState, value, valueIsLoading) => {
  if (isMounted && !category) {
    setState({
      productsArray: value,
      isLoadingProducts: valueIsLoading,
    });
  } else if (isMounted && category) {
    setState({
      categoriesArray: value.categories,
      isLoadingCategories: valueIsLoading,
    });
  }
};

export default updateDataCategoriesProducts;
