const handleCategoryProductsChange = (setState, e, updateCategoryName, updateData,
  categoriesArray) => {
  e.preventDefault();
  updateCategoryName(setState, e);
  updateData(e.target.value, 'updateProductCategory', categoriesArray.categories);
};

export default handleCategoryProductsChange;
