/* eslint-disable react/destructuring-assignment */
import React from 'react';
import postProductsCategory from '../api/post/postProductsCategory';
import getCategories from '../../editCategory/api/get/getCategories';

class InputEditProductsCategory extends React.Component {
  constructor(props) {
    super(props);
    const { category, id } = this.props;
    this.state = {
      id,
      categoryName: category,
    };
    this.updateData = this.updateData.bind(this);
  }

  componentDidMount() {
    this.interval = setTimeout(async () => {
      await getCategories(this.updateData, this.setError);
    }, 10);
  }

  async handleImageChange(e) {
    e.preventDefault();

    this.setState({
      categoryName: e.target.value,
    });
    postProductsCategory(this.state.id, e.target.value);
  }

  updateData(value) {
    this.setState({
      categoryName: value,
    });
  }

  render() {
    const { categoryName } = this.state;
    return (
      <>
        <select
          className="imageColors"
          onChange={(event) => this.setState({ categoryName: event.target.value })}
        >
          <option value="1" defaultValue>Фиолетовый</option>
          <option value="2">Коричневый</option>
          <option value="3">Зеленый</option>
        </select>
        <input
          type="text"
          value={categoryName}
          onChange={async (e) => {
            this.handleImageChange(e);
          }}
        />
      </>
    );
  }
}

export default InputEditProductsCategory;
