import getRequest from '../../../../common/api/get/getRequest';
import updateDataCategories from '../../../editProducts/utils/updateDataCategories';

const categoryPath = '/category';

async function getCategories(setState) {
  try {
    const response = await getRequest(categoryPath);
    const result = response.data;
    await updateDataCategories(setState, { categories: result }, true, 'updateCategories');
    return result;
  } catch (error) {
    return null;
  }
}

export default getCategories;
