import React from 'react';
import setPhotoImage from '../../untils/setPhotoImage';
import getCategories from '../api/get/getCategories';

class ImagePhoto extends React.Component {
  async componentDidMount() {
    this._isMounted = true;
    await getCategories(this.updateData, this.setError);
  }

  componentDidUpdate(prevProps, prevState) {
    const { isUpdated } = this.props;

    if (prevProps.isUpdated !== isUpdated) {
      this._isMounted = true;
      getCategories(this.updateData, this.setError);
    }
  }

  render() {
    const { className, imagePreviewUrl } = this.props;
    return (
      <img
        src={setPhotoImage(className, imagePreviewUrl)}
        className={className}
        title={className}
        alt={className}
      />
    );
  }
}

export default ImagePhoto;

