import { ADD, DELETE } from '../actions/catalogItemsActions';

const initialState = [];

function catalogItemsReducer(state = initialState, action) {
  switch (action.type) {
    case ADD:
      return [...state, action.payload.tasks];

    case DELETE:
      return [
        state.filter((item) => item.id !== action.payload.itemId),
      ];

    default:
      return state;
  }
}

export default catalogItemsReducer;
