import React from 'react';
import ImagePhotoProducts from '../../../common/image/components/ImagePhotoProductsComponent';
import EditProductsImage from './EditProductsImageComponent';

const ImageColumnEditProducts = ({
  isEditActive, product, isEditActiveId, updateImage,
}) => (
  <div className="columnImage">
    {(isEditActive && product.id) !== isEditActiveId
      ? (
        <>
          {product.image
            ? <ImagePhotoProducts className="imageProducts" product={product} />
            : <ImagePhotoProducts className="imageProductsEmpty" product={product} /> }
        </>
      ) : (
        <EditProductsImage
          id={product.id}
          description={product.description}
          updateImage={updateImage}
        />
      )}
  </div>
);

export default ImageColumnEditProducts;
